mapper.select:
select a.id,a.action_name,a.recruitment_quantity,a.participant_quantity,
    a.sign_up_start_time, a.sign_up_end_time,
    a.action_start_time, a.action_end_time, a.event_address
from action_information a
    left join user u1 on a.user_id = u1.id
    left join user u2 on a.create_by = u2.id


        and a.action_name like concat('%',#{actionName},'%')


        and a.create_by = #{releaseId}


        and u1.user_name like concat('%',#{releaseName},'%')


        and a.user_id = #{organizerId}


        and u2.user_name like concat('%',#{organizerName},'%')


        and a.sign_up_start_time >= #{signUpStartTimeStart}


        and a.sign_up_start_time <= #{signUpStartTimeEnd}



        and a.sign_up_end_time >= #{signUpEndTimeStart}


        and a.sign_up_end_time <= #{signUpEndTimeEnd}



        and a.action_start_time >= #{actionStartTimeStart}


        and a.action_start_time <= #{actionStartTimeEnd}



        and a.action_end_time >= #{actionEndTimeStart}


        and a.action_end_time <= #{actionEndTimeEnd}


        and a.event_address like concat('%',#{eventAddress},'%')

    and a.is_delete = 2

order by a.create_time

mapper.update:
update action_information
action_name = #{actionName}, activity_content = #{activityContent}, user_id = #{userId}, organizer_phone = #{organizerPhone}, recruitment_quantity = #{recruitmentQuantity}, sign_up_start_time = #{signUpStartTime}, sign_up_end_time = #{signUpEndTime}, action_start_time = #{actionStartTime}, action_end_time = #{actionEndTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
