mapper.select:
select s.id, s.sign_in_title, s.sign_in_content, s.start_time, s.end_time
from sign_in_information s
left join user u on s.create_by = u.id


        and s.create_by = #{userId}


        and u.user_name like concat('%', #{userName}, '%')


        and s.sign_in_title like concat('%', #{signInTitle}, '%')


        and s.start_time >= #{startTimeStart}


        and s.start_time <= #{startTimeEnd}


        and s.end_time >= #{endTimeStart}


        and s.end_time <= #{endTimeEnd}



        and FIND_IN_SET(#{signId}, s.user_ids) > 0

    and s.is_delete = 2

order by s.update_time desc
