mapper.update:
update datas
title = #{title}, icon = #{icon}, type = #{type}, content = #{content}, pageview = #{pageview}, points = #{points}, release_by = #{releaseBy}, release_time = #{releaseTime}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, is_delete = #{isDelete},
where id = #{id}

mapper.select:
select d.id, d.number, d.title, d.icon, d.content, d.points,d.pageview,
d.release_time as releaseTime,
d.status, d.update_time as updateTime
from datas d

    and type = #{type}

        and number like concat('%',#{number},'%')


        and title like concat('%',#{title},'%')

    and points = #{points}
    and status = #{status}

        and d.release_time >= #{releaseTimeStart}


        and d.release_time <= #{releaseTimeEnd}

    and d.is_delete = 2

order by create_time desc
