mapper.update:
update examination_information
set is_delete = 1
where id in

    #{item}

and type = #{type}

mapper.select:
select
id,examination_name as examinationName,question_quantity as questionQuantity,
start_time as startTime,end_time as endTime,update_time as updateTime
from examination_information


        and FIND_IN_SET(#{userId}, user_ids)



        and examination_name like concat('%', #{examinationName}, '%')


        and start_time >= #{startTimeStart}


        and start_time <= #{startTimeEnd}


        and end_time >= #{endTimeStart}


        and end_time <= #{endTimeEnd}

    and is_delete = 2 and type = 1

order by update_time desc
