mapper.update:
update question_bank
    set is_choose = #{status}
where id =

        #{item}

update question_bank
set times = times + #{times}
where id = #{addQuestionId}

mapper.select:
select id, type, question,is_choose,times, update_time
from question_bank

        and type = #{type}
        and question like concat('%',#{question},'%')
        and is_choose = #{isChoose}
        and is_delete = 2

select q.type, q.question, q.question_option, q.answer_type, q.is_choose, u.user_name as updateName, q.update_time ,q.note from question_bank q left join user u on q.update_by = u.id where q.id in
#{item}
and q.is_delete = 2
