mapper.update:
update question_bank
type = #{type}, question = #{question}, question_option = #{questionOption}, answer_type = #{answerType}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select id, type, question,is_choose,update_time
from question_bank

        and type = #{type}
        and question like concat('%',#{question},'%')
        and is_choose = #{isChoose}
        and is_delete = 2
